// page init
$(function(){
	$(window).scroll(function(){
		if ($(this).scrollTop() > 100) {
			$('.scrollup').fadeIn();
		} else {
			$('.scrollup').fadeOut();
		}
	});
	jcf.customForms.replaceAll();
	initCycleCarousel();
	jQuery('input, textarea').placeholder();
	initBackToTop();
	initMenudraw();	
});


